var filtro=document.querySelector("#filtra-tabela");filtro.addEventListener("input",function(e){console.log(this.value);var t=document.querySelectorAll(".cadastro");if(this.value.length>0)for(var r=0;r<t.length;r++){var l=t[r];l.querySelector(".tabela-nome");l.textContent.includes(this.value)?l.classList.remove("sumir"):l.classList.add("sumir")}else for(var r=0;r<t.length;r++){var l=t[r];l.classList.remove("sumir")}});